import { StatusBar } from 'expo-status-bar';
import { useEffect, useState } from 'react';
import { StyleSheet, Text, View, ActivityIndicator, Button, TextInput } from 'react-native';
import styles from '../styles'

function FetchPosts() {

    /*
    useStates para o loading do app e os dados do arquivo json.
    o loading serve apenas para indicar ao usuário que algo está carregando, nesse caso,
    a requisição do app para pegar o conteúdo do servidor localhost:3001 na rota /cad
    */
    const [isLoading, setLoading] = useState(true);
    const [posts, setPosts] = useState([]);
    const [postId, setPostId] = useState('');


    function getPosts(){
        fetch('http://localhost:3001/posts')
        .then(res => res.json())
        .then(result => {
            setPosts(result);
            setLoading(false);
        })
        .catch(err => console.log(err));
    }

/*     function getPostById(id){
        setLoading(true);
        fetch('http://localhost:3001/posts/'+id)
        .then(res => res.json())
        .then(result => {
            setPosts(result);
            setLoading(false);
        })
        .catch(err => console.log(err));
    }
 */
    function display(){
        getPosts();
        if(isLoading) {
            return <ActivityIndicator size="large"/>
        }
        else {
            return posts.map((post)=>{
                return (
                    <View style={styles.post_cell} >
                        <Text style={styles.titulo}>{post.titulo}</Text>
                        <Text style={styles.conteudo}>{post.conteudo}</Text>
                    </View>

                );
            })
        }

    }


    return(
        <View style={styles.container}>
            {display()}
            <TextInput style={styles.input} placeholder='Insert ID' value={postId} onChangeText={(value)=>setPostId(value)}/>
            <Button title='get by id' onPress={()=>getPostById(postId)} />
        </View>

    )


}

export default FetchPosts;
