import {React} from 'react';
import {StyleSheet} from 'react-native';

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    post_cell:{
      display: 'flex',
      alignItems: 'flex-start',
      margin: '10px',
  
    },
    titulo:{
      fontWeight : 'bold',
      fontSize : '30px'
    },
    conteudo:{
      fontSize : '20px'
    },
    input:{
        width : '30px',
        height: '30px',
        padding : '10px',
        margin : '10px',
    }
  });

  export default styles;
