import { StatusBar } from 'expo-status-bar';
import { useEffect, useState } from 'react';
import { StyleSheet, Text, View, ActivityIndicator, Button, TextInput } from 'react-native';
import styles from '../styles'

function Cadastro(){


    

    function cadastrar(){
        fetch('http://localhost:3001/posts', {
            method: 'POST',
            headers:{
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                titulo: titulo,
                conteudo: conteudo
            })
        }).then(res => res.json())
        .then(res=>console.log(res))
        .catch(err => console.log(err))
        ;
    }    

    return (
        <>
        <View>
            <Text>TITULO</Text>
            <TextInput onChangeText={(titulo)=>setTitulo(titulo)} />
        </View>
        <View>
            <Text>CONTEUDO</Text>
            <TextInput onChangeText={(conteudo)=>setConteudo(conteudo)} />
        </View>
        <View>
            <Button onPress={()=>cadastrar()} title='Cadastrar'/>
        </View>
        </>
    );
}

export default Cadastro;
