import { StatusBar } from 'expo-status-bar';
import { useEffect, useState } from 'react';
import { StyleSheet, Text, View, ActivityIndicator, Button, TextInput, FlatList } from 'react-native';
import styles from '../styles'

function Feed() {


    const [isLoading, setLoading] = useState(true);
    const [posts, setPosts] = useState([]);
    const [searchInput, setSearchInput] = useState('');


    useEffect(()=>{
        fetch('http://localhost:3001/posts')
        .then(res => res.json())
        .then(result => {
            setPosts(result);
            setLoading(false);
        })
        .catch(err => console.log(err));
    });


    function renderPost({post, index}){
        return (
        <View><Text>{post.title}</Text></View>
        );
    }


    return(
        <View style={styles.container}>
            <Text style={styles.titulo}>Meu Feed</Text>
            <TextInput style={styles.input} placeholder='Search Your Posts' onChangeText={(text)=>setSearchInput(text)} />
            <Text>{searchInput}</Text>
            {
                posts.length <= 0 ?
                <ActivityIndicator size='large'/>
                :
                <FlatList style={styles.post_cell}
                data = {posts}
                keyExtractor={(post, index)=> {return post.id.toFixed()}}
                renderItem = {({post, index})=>
                    (
                    <View><Text>{post.titulo}</Text></View>
                    )
                }

                />

            }
        </View>

    )


}

export default Feed;
