import {useState, useEffect} from 'react';
import { 
    StyleSheet, 
    Text, 
    View, 
    SafeAreaView, 
    ActivityIndicator ,
} from 'react-native';

function App() {

    const [isLoading, setLoading] = useState(true);
    const [data, setData] = useState('');

    useEffect(
        ()=>{
            fetch('http://localhost:3001/cad')
            .then(res => res.json())
            .then(
                result => {
                    setLoading(false);
                    setData(result);
                }
            )
            .catch(err => {alert(err)});
        },
        []
    )


    function getContent(){
        if(isLoading){
            return <ActivityIndicator size='large'/>
        }

        /* console.log(data); */
        return <Text>{JSON.parse(data.content).nome}</Text>

    }

    return (
    <View style={styles.container}>
        {getContent()}
    </View>
    )




}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default App;
