import { StatusBar } from 'expo-status-bar';
import { useEffect, useState } from 'react';
import { StyleSheet, Text, View, ActivityIndicator, FlatList } from 'react-native';
import styles from '../styles'

function FetchPosts() {

    /*
    useStates para o loading do app e os dados do arquivo json.
    o loading serve apenas para indicar ao usuário que algo está carregando, nesse caso,
    a requisição do app para pegar o conteúdo do servidor localhost:3001 na rota /cad
    */
    const [isLoading, setLoading] = useState(true);
    const [posts, setPosts] = useState([]);

    useEffect(()=> {
        fetch('http://localhost:3001/posts')
        .then(res => res.json())
        .then(result => {
            setPosts(result);
            setLoading(false);
        })
        .catch(err => console.log(err))
    });

    function getPostById(){
        const post = posts.filter((post)=> post.id == 2);
    }

    function display(){
        if(isLoading) return <ActivityIndicator size="large"/>
        else {
            return posts.map((post)=>{
                return (
                    <View style={styles.post_cell} >
                        <Text style={styles.titulo}>{post.titulo}</Text>
                        <Text style={styles.conteudo}>{post.conteudo}</Text>
                    </View>

                );
            })
        }

    }


    return(
        <View style={styles.container}>
            {display()}
        </View>
    )


}

export default FetchPosts;
