import { StatusBar } from 'expo-status-bar';
import { useEffect, useState } from 'react';
import { StyleSheet, Text, View, ActivityIndicator, Button, TextInput, FlatList } from 'react-native';
import styles from '../styles'


function Feed() {


    const [isLoading, setLoading] = useState(true);
    const [posts, setPosts] = useState([]);
    const [searchInput, setSearchInput] = useState('');


    useEffect(()=>{
        fetch('http://localhost:3001/posts')
        .then(res => res.json())
        .then(result => {
            setPosts(result);
            setLoading(false);
        })
        .catch(err => console.log(err));
    });


    return(
        <View style={styles.container}>
            <Text style={styles.titulo}>Meu Feed</Text>
            <TextInput style={styles.input} placeholder='Search Your Posts' onChangeText={(text)=>setSearchInput(text)} />
            <Text>{searchInput}</Text>
            {
                posts.length <= 0 ?
                <ActivityIndicator size='large'/>
                :
                /* console.log(posts) */
                /*
                Temos que 'desestruturar' o nosso objeto post colocando-o entre
                chaves no renderItem pois o renderItem retorna um valor totalmente diferente
                ao usar o nosso array posts, e dentro desse valor h√° o nosso item post
                */
                <FlatList
                data={posts}
                renderItem={({ post })=>{return (
                    <Text>{post.titulo}</Text>
                )}}
                />

            }
        </View>

    )


}

export default Feed;
