import { StatusBar } from 'expo-status-bar';
import { useEffect, useState } from 'react';
import { StyleSheet, Text, View, ActivityIndicator, Button, TextInput, FlatList } from 'react-native';
import styles from '../styles'

function Login(){

    
    const [isLoading, setLoading] = useState(true);
    const [usuarios, setUsuarios] = useState([]);
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');

/*     useEffect(() =>
    {
        fetch('http://localhost:3001/usuarios')
        .then(res => res.json())
        .then(result => {
            setUsuarios(result)
            setLoading(false);
        })
    }
    ) */

    function login(){
        fetch('http://localhost:3001/usuarios', {
            method: 'POST',
            headers: { 
                'Content-Type':'application/json'
            },
            body: JSON.stringify({
                email: email,
                senha: senha
            })
        })
    }

    return (

        <View style={styles.container}>
            
            <Text style={styles.titulo}>Ol√°!</Text>
            <View>
                <Text>E-Mail</Text>
                <TextInput style={styles.input} placeholder='exemplo@mail.com' onChangeText={(email)=>setEmail(email)} />
            </View>
            <View>
                <Text>Senha</Text>
                <TextInput style={styles.input} onChangeText={(senha)=>setSenha(senha)} />
            </View>
            <View>
                <Button title='Login' onPress={()=>login()} />
            </View>
            
        )
        }
            
        </View>

    )

}

export default Login;
