//Nosso contexto para a autenticação
/*
O context basicamente serve para possibilitar que todos os componentes de um certo código recebam
os mesmos parâmetros do Context, evitando assim ter que repassá-los nível por nível nos comps até chegar
no que você quer que tenha esse param
*/

import { createContext } from 'react';

const AuthContext = createContext({signed:true});

export default AuthContext;
